from Crypto.Util.number import *

flag = b'xxxx'  # flag=b'flag{xxxxxxx}'
m = bytes_to_long(flag)
p, q = getPrime(512), getPrime(512)
n = p*q
e1, e2 = getPrime(32), getPrime(32)
c1 = pow(m, e1, n)
c2 = pow(m, e2, n)
print("n =", n)
print("c1 =", c1)
print("c2 =", c2)
print("e1 =", e1)
print("e2 =", e2)

